const ON = "1";
const OFF = "0";

var dateTime = new Date();
var secondsNow = dateTime.getTime();
var gameDuration = 0;

var state = flow.get("mazeState");
if (state === undefined) //test exists
{
    msg.contextData = "ERROR getting context data";
    return msg;
}else{

    state.gameState.state = msg.payload;

    /// MQTT settings
    msg.qos = 0;
    msg.retain = false;
    msg.topic = "lasermaze/" + state.gameState.TXNode + "/output/" + state.gameState.TXPort + "/";

    if(msg.payload === "state_safe"){
        state.gameState.stateTX = OFF;
        state.gameState.startTime = 0;
    }else if(msg.payload === "state_armed"){
        state.gameState.stateTX = ON;
        state.gameState.startTime = secondsNow;
    }else if(msg.payload === "state_end"){
        state.gameState.stateTX = OFF;
